        public void SetCalendarItem (McEvent e, CalendarItemEditorAction action)
        {
            this.e = e;
            this.action = action;
            if (null == e) {
                this.item = null;
            } else {
                this.item = McCalendar.QueryById<McCalendar> (e.CalendarId);
            }
        }

