        public override bool CanSubmitFields ()
        {
            if (usernameField.Text.Trim().Length == 0) {
                return false;
            }
            if (serverField.Text.Trim().Length == 0) {
                return false;
            }
            if (domainField.Text.Trim().Length == 0) {
                return false;
